      //     <Label>51</Label>
      //     My Bookshelf
      //   </Menu.Item>

      //   <Menu.Item
      //     as={NavLink}
      //     to="/following"
      //     name="following"
      //     active={activeItem === "following"}
      //     onClick={this.handleItemClick}
      //   >
      //     <Label>1</Label>
      //     Following
      //   </Menu.Item>
      //   <Menu.Item>
      //     <Input
      //       id="searchBar"
      //       icon="search"
      //       placeholder="Search bookshelves..."
      //       onChange={this.handleFieldChange}
      //       onKeyDown={this.add}
      //     />
      //   </Menu.Item>
      // </Menu>
    );
  }
}
// export default NavBar
export default withRouter(NavBar);
