import React from "react";
import { Card, Icon, Image, Button, Grid } from "semantic-ui-react";
//grid nor card group is working? take off description and try again?

{/* <Grid.Column key={4}> </Grid.Column> */}
class Search extends React.Component {
  render() {
    return (
      // <Card.Group itemsPerRow={4}>
        <Card class="ui six cards">
          {/* <Image
          // can I do ternary statement? so this will contain to work?
            src={this.props.searchProp.volumeInfo.imageLinks.smallThumbnail}
            wrapped
            ui={false}
            n
          /> */}
          <Card.Content>
            <Card.Header>{this.props.searchProp.volumeInfo.title}</Card.Header>
            <Card.Meta>
              <span>{this.props.searchProp.volumeInfo.authors}</span>
            </Card.Meta>
            <Card.Description>
              {/* {this.props.searchProp.volumeInfo.description} */}
            </Card.Description>
          </Card.Content>
          <Button animated>
            <Button.Content visible>Add to Shelf</Button.Content>
            <Button.Content hidden>
              <Icon name="book" />
            </Button.Content>
          </Button>
        </Card>
      // </Card.Group>
    );
  }
}
export default Search;
