import React, { Component } from "react";
import SearchResultsCard from "./SearchResultsCard";
import BookManager from "../../modules/BookManager";
// import { Button, Icon } from "semantic-ui-react";

class SearchList extends Component {
  state = {
    ratings: [],
  };

  componentDidMount() {
    BookManager.getAll().then((ratings) => {
      this.setState({
        ratings: ratings,
      });
    });
  }

  render() {
    return (
      <>
     <div> 
       {this.state.ratings.map((currentEventInLoop) => {
            return (

              <EventCard
              
                key={currentEventInLoop.id}
                eventProp={currentEventInLoop}
              />)})}
            
            
              </div>
              <div>
        {this.props.history.location.state.detail.map((currentBookInLoop) => {
          return (
            <SearchResultsCard
              key={currentBookInLoop.id}
              searchProp={currentBookInLoop}
            />
           
          );
        })}
         </div>
      </>
    );
  }
}

export default SearchList;
