import React from "react";
import { Card, Icon, Image, Button, Grid } from "semantic-ui-react";

class Search extends React.Component {
  render() {
    return (
      <Grid.Column key={4}>
        <Card>
          <Image
          // can I do teranarary statement?
            src={this.props.searchProp.volumeInfo.imageLinks.smallThumbnail}
            wrapped
            ui={false}
            n
          />
          <Card.Content>
            <Card.Header>{this.props.searchProp.volumeInfo.title}</Card.Header>
            <Card.Meta>
              <span>{this.props.searchProp.volumeInfo.authors}</span>
            </Card.Meta>
            <Card.Description>
              {this.props.searchProp.volumeInfo.description}
            </Card.Description>
          </Card.Content>
          <Button animated>
            <Button.Content visible>Add to Shelf</Button.Content>
            <Button.Content hidden>
              <Icon name="book" />
            </Button.Content>
          </Button>
        </Card>
      </Grid.Column>
    );
  }
}
export default Search;
